-- Import dmc_multitouch by David McCuskey MultiTouch = require("dmc_multitouch");-- Hide status bardisplay.setStatusBar(display.HiddenStatusBar);-- Background imagelocal background = display.newImageRect("images/background.png", 640, 960); background.x = display.contentCenterX;background.y = display.contentCenterY;-- Square outlinelocal sqLine = display.newImageRect("images/sqLine.png", 228, 228); sqLine.x = 473;sqLine.y = 181;-- Squarelocal square = display.newImageRect("images/square.png", 188, 188); square.x = 144;square.y = 778;-- Circle outlinelocal circLine = display.newImageRect("images/circLine.png", 245, 245); circLine.x = 181;circLine.y = 180;-- circle positioning local circle = display.newImageRect("images/circle.png", 200, 200); circle.x = 473;circle.y = 778;-- myText positioning local myText = display.newImageRect("images/myText.png", 508, 78); myText.x = 311;myText.y = 475;-- CircleMultiTouch.activate(circle, "move", "single");-- Set initial variables to 0local circlePosX = 0;local circlePosY = 0;-- User drag interaction on blue circlelocal function circleDrag (event)	local t = event.target	--If user touches & drags circle, follow the user's touch	if event.phase == "moved" then		circlePosX = circle.x - circLine.x; 		circlePosY = circle.y - circLine.y;		if (circlePosX < 0) then			circlePosX = circlePosX * -1;			end			if (circlePosY < 0) then				circlePosY = circlePosY * -1;			end			-- If user drags circle within 50 pixels of center of outline, snap into middle			if (circlePosX <= 50) and (circlePosY <= 50) then				circle.x = circLine.x;				circle.y = circLine.y;			end			-- When the stops dragging circle within 50 pixels of center of outline, snap into middle, and...	elseif event.phase == "ended" then		if (circlePosX <= 50) and (circlePosY <= 50) then			circle.x = circLine.x;			circle.y = circLine.y;			-- ...lock circle into place where it cannot be moved.			MultiTouch.deactivate(circle);		end	end	return true;end circle:addEventListener(MultiTouch.MULTITOUCH_EVENT, circleDrag);-- Same actions for the square as the circleMultiTouch.activate(square, "move", "single");local squarePosX = 0;local squarePosY = 0;local function squareDrag (event)	local t = event.target	if event.phase == "moved" then		squarePosX = square.x - sqLine.x;		squarePosY = square.y - sqLine.y;		if (squarePosX < 0) then			squarePosX = squarePosX * -1;		end		if (squarePosY < 0) then			squarePosY = squarePosY * -1;		end				if (squarePosX <= 50) and (squarePosY <= 50) then			square.x = sqLine.x;			square.y = sqLine.y;		end	elseif event.phase == "ended" then		if (squarePosX <= 50) and (squarePosY <= 50) then			square.x = sqLine.x;			square.y = sqLine.y;			-- If you'd like to be able to move the square again, comment out the line below			MultiTouch.deactivate(square);		end	end	return true;endsquare:addEventListener(MultiTouch.MULTITOUCH_EVENT, squareDrag);